import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import { Row,Form,Button } from 'react-bootstrap'
import { createOrder } from '../actions/orderActions'
import Message from '../components/Message'
import SubDate from '../components/SubDate'

function BecomePro({history}) {

    const orderCreate = useSelector(state => state.orderCreate)
    const { order, error, success } = orderCreate

    const dispatch = useDispatch()

    const selectedSub = useSelector(state => state.selectedSub)
    const {subItem,paymentMethod}  = selectedSub

    if (!paymentMethod) {
        history.push('/payment')
    }

    useEffect(() => {
        if (success) {
            history.push(`/order/${order._id}`)
        }
    }, [success, history])

    const placeOrder = () =>{
        dispatch(createOrder(
            subItem.name,paymentMethod,subItem.price
        ))
    }

    return (
        <div>
            <h1> Why Become Pro ? </h1>
            <p>You definitely should</p>
            <Row>
                <Form method="post">
                    <label>Membership Type</label>
                    <br/>
                    <Button
                        type='button'
                        className='btn-block'
                        onClick= {placeOrder}
                    >
                        Pay {subItem.price}
                    </Button>

                    {error && <Message variant='danger'>{error}</Message>}
                    
                </Form>
            </Row>
        </div>
    )
}

export default BecomePro
