import {
    ADD_SUBSCRIPTION,
    SAVE_PAYMENT_METHOD,
} from '../constants/orderConstants'


export const selectedSubReducer = (state = { subItem: [] }, action) => {
    switch (action.type) {
        case ADD_SUBSCRIPTION:
            return {subItem: action.payload} 

        case SAVE_PAYMENT_METHOD:
            return {
                ...state,
                paymentMethod: action.payload
            }

        default:
            return state
    }
}
